mapper.select:
select client_id,set_cd,tax,accordion,price_on_delivery_note,delivery_note_type,version
from Mc105_product_setting
where client_id = #{client_id}

	and set_cd = #{set_cd}

mapper.update:
update Mc105_product_setting set
		accordion = #{accordion}, tax = #{tax}
 where client_id = #{client_id}

update Mc105_product_setting
set price_on_delivery_note = #{price_on_delivery_note}, delivery_note_type =#{delivery_note_type}
where client_id = #{client_id}
